package com.a91zsc.www.myapplication.application;import android.bluetooth.BluetoothDevice;import android.content.BroadcastReceiver;import android.content.Context;import android.content.Intent;import android.os.Message;import android.util.Log;import com.a91zsc.www.myapplication.view.PrintDataActivity;/** * Created by yangx on 17.8.28. */public class IntentConnection extends BroadcastReceiver {    public static int network = 0;    public static boolean isno = true;    @Override    public void onReceive(Context context, Intent intent) {        String action = intent.getAction();        if(action.equals("android.netWork.onclose")){            network = 1;        }    }}