package com.a91zsc.www.myapplication.util;import android.content.Context;import android.net.Uri;import android.os.Handler;import android.webkit.WebView;import java.io.BufferedReader;import java.io.ByteArrayOutputStream;import java.io.InputStream;import java.io.InputStreamReader;import java.net.HttpURLConnection;import java.net.URL;import java.util.Timer;import java.util.TimerTask;import android.os.Message;import android.widget.Button;import android.widget.Toast;import net.sf.json.JSONObject;import static android.R.attr.button;import static android.R.attr.factor;import static android.R.attr.path;import static java.net.Proxy.Type.HTTP;/** * Created by yangx on 17.6.20. */public class XMLTool {    private String data = null;    /**     * 提示显示时间     * @param toast     * @param cnt     */    public void showMyToast(final Toast toast, final int cnt) {        final Timer timer = new Timer();        timer.schedule(new TimerTask() {            @Override            public void run() {                toast.show();            }        }, 0, 3000);        new Timer().schedule(new TimerTask() {            @Override            public void run() {                toast.cancel();                timer.cancel();            }        }, cnt );    }}