package com.a91zsc.www.myapplication.util;import android.bluetooth.BluetoothDevice;import android.bluetooth.BluetoothSocket;import android.content.Context;import android.util.Log;import android.widget.Toast;import java.io.OutputStream;import java.util.UUID;/** * Created by yangx on 17.8.17. */public class bluetoothUtil {    public static final UUID uuid = UUID.fromString("00001101-0000-1000-8000-00805F9B34FB");    private BluetoothSocket bluetoothSocket = null;    private OutputStream outputStream = null;    boolean isconntent = false;    printUtils pu = new printUtils();    toolsFileIO fileIO = new toolsFileIO();    public boolean connect(Context context, BluetoothDevice device) {        Log.e("adderstrig", device.getAddress() + ":" + device);        try {            bluetoothSocket = device.createRfcommSocketToServiceRecord(uuid);            bluetoothSocket.connect();            outputStream = bluetoothSocket.getOutputStream();            pu.outputGet(outputStream);            fileIO.putBlueToothDrive(context, device.toString());            isconntent = true;        } catch (Exception e) {            isconntent = false;        }        Log.e("哈哈哈", isconntent + "");        return isconntent;    }}