package com.a91zsc.www.myapplication.util;import com.a91zsc.www.myapplication.R;/** * Created by yangx on 17.6.19. */public class utilsTools {    /**     * Created by yangx on 17.6.7.     */        private static final int MIN_CLICK_DELAY_TIME = 2000;        private static long lastClickTime;        public static boolean isFastClick() {            boolean flag = false;            long curClickTime = System.currentTimeMillis();            if ((curClickTime - lastClickTime) >= MIN_CLICK_DELAY_TIME) {                flag = true;            }            lastClickTime = curClickTime;            return flag;        }}