package com.a91zsc.www.myapplication.util;import android.bluetooth.BluetoothAdapter;import android.bluetooth.BluetoothDevice;import android.content.BroadcastReceiver;import android.content.Context;import android.content.Intent;import android.content.IntentFilter;import com.a91zsc.www.myapplication.R;/** * Created by yangx on 17.6.19. */public class utilsTools {    /**     * Created by yangx on 17.6.7.     */        private static final int MIN_CLICK_DELAY_TIME = 3000;        private static long lastClickTime;        public static boolean isFastClick() {            boolean flag = false;            long curClickTime = System.currentTimeMillis();            if ((curClickTime - lastClickTime) >= MIN_CLICK_DELAY_TIME) {                flag = true;            }            lastClickTime = curClickTime;            return flag;        }}